        st = "" 
        for x in sq: 
            st = x + st
        return st

    #Then generate the complementary sequence
    def reverse_oligo(st):
        self.reverse_sq=""
        for bp in st:
            if bp == "A":
              reverse_sq + "T"
            elif bp == "G":
              reverse_sq + "C"
            elif bp =="T":
              reverse_sq + "A"
            elif bp =="C":
              reverse_sq + "G"
        return self.reverse_sq

    def __str__(self):
        return  "forward oligo sequence is", (self.sq) +\
